    @Test
    public void testEmailRegEx() {
        Matcher m = VALID_EMAIL_ADDRESS_REGEX.matcher("test@test.com");
        assertTrue(m.matches());
        m = VALID_EMAIL_ADDRESS_REGEX.matcher("test+1@test.com");
        assertTrue(m.matches());

        assertTrue(ValidateUtils.isValidEmailAddress("test@test.com"));
        assertTrue(ValidateUtils.isValidEmailAddress("test+1@test.com"));
        //assertTrue(ValidateUtils.isValidEmailAddress("test_1@test.com"));  fails
        assertTrue(VALID_EMAIL_ADDRESS_REGEX.matcher("test_1@test.com").matches());
        assertFalse(ValidateUtils.isValidEmailAddress("test_1.xxx.com"));
        assertFalse(ValidateUtils.isValidEmailAddress("test_1.xxx@xxx"));
        assertFalse(ValidateUtils.isValidEmailAddress("!test@test.com"));
    }

    @Test
    public void testPermuations() {
        String s1Array[] = new String[]{"A", "B", "C"};
        String s2Array[] = new String[]{"1", "2", "3"};
        String s3Array[] = new String[]{"!", "#", "%"};

        java.util.List<java.util.Collection<String>> lists = new LinkedList<Collection<String>>();

        java.util.Collection<String> l1 = Arrays.asList(s1Array);
        java.util.Collection<String> l2 = Arrays.asList(s2Array);
        java.util.Collection<String> l3 = Arrays.asList(s3Array);

        lists.add(l1);
        lists.add(l2);
        lists.add(l3);

        java.util.Collection<List<String>> obj = CommonUtils.permutations(lists);

        for (List<String> l : obj) {
            Log.info("l:" + l.toString());
        }

    }
